cit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";a:12:{s:6:\"server\";s:5:\"nginx\";s:4:\"date\";s:29:\"Sat, 17 Oct 2015 14:10:58 GMT\";s:12:\"content-type\";s:23:\"text/xml; charset=UTF-8\";s:10:\"connection\";s:5:\"close\";s:4:\"vary\";s:15:\"Accept-Encoding\";s:25:\"strict-transport-security\";s:11:\"max-age=360\";s:7:\"expires\";s:29:\"Sat, 17 Oct 2015 14:23:57 GMT\";s:13:\"cache-control\";s:0:\"\";s:6:\"pragma\";s:0:\"\";s:13:\"last-modified\";s:31:\"Sat, 17 Oct 2015 13:48:57 +0000\";s:15:\"x-frame-options\";s:10:\"SAMEORIGIN\";s:4:\"x-nc\";s:11:\"HIT lax 249\";}s:5:\"build\";s:14:\"20151017132102\";}','no'),(3854,'_transient_timeout_feed_mod_b9388c83948825c1edaef0d856b7b109','1445133484','no'),(3855,'_transient_feed_mod_b9388c83948825c1edaef0d856b7b109','1445090284','no'),(3856,'_transient_timeout_plugin_slugs','1445179936','no'),(3857,'_transient_plugin_slugs','a:0:{}','no'),(3858,'_transient_timeout_dash_88ae138922fe95674369b1cb3d215a2b','1445133484','no'),(3859,'_transient_dash_88ae138922fe95674369b1cb3d215a2b','<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/09/wordpress-4-3-1/\'>WordPress 4.3.1 Security and Maintenance Release</a> <span class=\"rss-date\">September 15, 2015</span><div class=\"rssSummary\">WordPress 4.3.1 is now available. This is asecurity releasefor all previous versions and we strongly encourage you to update your sites immediately. This release addresses three issues, including twocross-site scripting vulnerabilities and a potential privilege escalation. WordPress versions 4.3 and earlier are vulnerable to a cross-site scripting vulnerability when processing shortcode tags (CVE-2015-5714). Reported by [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://poststatus.com/playing-with-type/\'>Post Status: Playing with type</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/work-life-balance-draft-podcast/\'>Post Status: Work life balance  Draft podcast</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/akismet-3-1-5-fixes-critical-xss-security-vulnerability\'>WPTavern: Akismet 3.1.5 Fixes Critical XSS Security Vulnerability</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/google-analytics-dashboard-for-wp/\' class=\'dashboard-news-plugin-link\'>Google Analytics Dashboard for WP</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=google-analytics-dashboard-for-wp&amp;_wpnonce=7f2ecbbe62&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Google Analytics Dashboard for WP\'>Install</a>)</span></li></ul></div>','no'),(3860,'_site_transient_timeout_popular_importers_en_US','1445263132','yes'),(3861,'_site_transient_popular_importers_en_US','a:2:{s:9:\"importers\";a:8:{s:7:\"blogger\";a:4:{s:4:\"name\";s:7:\"Blogger\";s:11:\"description\";s:86:\"Install the Blogger importer to import posts, comments, and users from a Blogger blog.\";s:11:\"plugin-slug\";s:16:\"blogger-importer\";s:11:\"importer-id\";s:7:\"blogger\";}s:9:\"wpcat2tag\";a:4:{s:4:\"name\";s:29:\"Categories and Tags Converter\";s:11:\"description\";s:109:\"Install the category/tag converter to convert existing categories to tags or tags to categories, selectively.\";s:11:\"plugin-slug\";s:18:\"wpcat2tag-importer\";s:11:\"importer-id\";s:9:\"wpcat2tag\";}s:11:\"livejournal\";a:4:{s:4:\"name\";s:11:\"LiveJournal\";s:11:\"description\";s:82:\"Install the LiveJournal importer to import posts from LiveJournal using their API.\";s:11:\"plugin-slug\";s:20:\"livejournal-importer\";s:11:\"importer-id\";s:11:\"livejournal\";}s:11:\"movabletype\";a:4:{s:4:\"name\";s:24:\"Movable Type and TypePad\";s:11:\"description\";s:99:\"Install the Movable Type importer to import posts and comments from a Movable Type or TypePad blog.\";s:11:\"plugin-slug\";s:20:\"movabletype-importer\";s:11:\"importer-id\";s:2:\"mt\";}s:4:\"opml\";a:4:{s:4:\"name\";s:8:\"Blogroll\";s:11:\"description\";s:61:\"Install the blogroll importer to import links in OPML format.\";s:11:\"plugin-slug\";s:13:\"opml-importer\";s:11:\"importer-id\";s:4:\"opml\";}s:3:\"rss\";a:4:{s:4:\"name\";s:3:\"RSS\";s:11:\"description\";s:58:\"Install the RSS importer to import posts from an RSS feed.\";s:11:\"plugin-slug\";s:12:\"rss-importer\";s:11:\"importer-id\";s:3:\"rss\";}s:6:\"tumblr\";a:4:{s:4:\"name\";s:6:\"Tumblr\";s:11:\"description\";s:84:\"Install the Tumblr importer to import posts &amp; media from Tumblr using their API.\";s:11:\"plugin-slug\";s:15:\"tumblr-importer\";s:11:\"importer-id\";s:6:\"tumblr\";}s:9:\"wordpress\";a:4:{s:4:\"name\";s:9:\"WordPress\";s:11:\"description\";s:130:\"Install the WordPress importer to import posts, pages, comments, custom fields, categories, and tags from a WordPress export file.\";s:11:\"plugin-slug\";s:18:\"wordpress-importer\";s:11:\"importer-id\";s:9:\"wordpress\";}}s:10:\"translated\";b:0;}','yes'),(3872,'category_children','a:0:{}','yes'),(3874,'_transient_timeout_acf_pro_get_remote_info','1445133862','no'),(3875,'_transient_acf_pro_get_remote_info','a:15:{s:4:\"name\";s:26:\"Advanced Custom Fields Pro\";s:4:\"slug\";s:26:\"advanced-custom-fields-pro\";s:8:\"homepage\";s:36:\"http://www.advancedcustomfields.com/\";s:7:\"version\";s:5:\"5.3.1\";s:6:\"author\";s:13:\"elliot condon\";s:10:\"author_url\";s:28:\"http://www.elliotcondon.com/\";s:12:\"contributors\";s:12:\"elliotcondon\";s:8:\"requires\";s:5:\"3.6.0\";s:6:\"tested\";s:3:\"4.3\";s:4:\"tags\";a:31:{i:0;s:5:\"5.3.0\";i:1;s:5:\"5.2.9\";i:2;s:5:\"5.2.8\";i:3;s:5:\"5.2.7\";i:4;s:5:\"5.2.6\";i:5;s:5:\"5.2.5\";i:6;s:5:\"5.2.4\";i:7;s:5:\"5.2.3\";i:8;s:5:\"5.2.2\";i:9;s:5:\"5.2.1\";i:10;s:5:\"5.2.0\";i:11;s:5:\"5.1.9\";i:12;s:5:\"5.1.8\";i:13;s:5:\"5.1.7\";i:14;s:5:\"5.1.6\";i:15;s:5:\"5.1.5\";i:16;s:5:\"5.1.4\";i:17;s:5:\"5.1.3\";i:18;s:5:\"5.1.2\";i:19;s:5:\"5.1.1\";i:20;s:5:\"5.1.0\";i:21;s:5:\"5.0.9\";i:22;s:5:\"5.0.8\";i:23;s:5:\"5.0.7\";i:24;s:5:\"5.0.6\";i:25;s:5:\"5.0.5\";i:26;s:5:\"5.0.4\";i:27;s:5:\"5.0.3\";i:28;s:5:\"5.0.2\";i:29;s:5:\"5.0.1\";i:30;s:5:\"5.0.0\";}s:6:\"tagged\";s:123:\"acf, advanced, custom, field, fields, custom field, custom fields, simple fields, magic fields, more fields, repeater, edit\";s:11:\"description\";s:4328:\"<p>Advanced Custom Fields is the perfect solution for any WordPress website which needs more flexible data like other Content Management Systems. </p>\n<ul><li>Visually create your Fields</li><li>Select from multiple input types (text, textarea, wysiwyg, image, file, page link, post object, relationship, select, checkbox, radio buttons, date picker, true / false, repeater, flexible content, gallery and more to come!)</li><li>Assign your fields to multiple edit pages (via custom location rules)</li><li>Easily load data through a simple and friendly API</li><li>Uses the native WordPress custom post type for ease of use and fast processing</li><li>Uses the native WordPress metadata for ease of use and fast processing</li></ul>\n<h4> Field Types </h4>\n<ul><li>Text (type text, api returns text)</li><li>Text Area (type text, api returns text)</li><li>Number (type number, api returns integer)</li><li>Email (type email, api returns text)</li><li>Password (type password, api returns text)</li><li>WYSIWYG (a WordPress wysiwyg editor, api returns html)</li><li>Image (upload an image, api returns the url)</li><li>File (upload a file, api returns the url)</li><li>Select (drop down list of choices, api returns chosen item)</li><li>Checkbox (tickbox list of choices, api returns array of choices)</li><li>Radio Buttons ( radio button list of choices, api returns chosen item)</li><li>True / False (tick box with message, api returns true or false)</li><li>Page Link (select 1 or more page, post or custom post types, api returns the selected url)</li><li>Post Object (select 1 or more page, post or custom post types, api returns the selected post objects)</li><li>Relationship (search, select and order post objects with a tidy interface, api returns the selected post objects)</li><li>Taxonomy (select taxonomy terms with options to load, display and save, api returns the selected term objects)</li><li>User (select 1 or more WP users, api returns the selected user objects)</li><li>Google Maps (interactive map, api returns lat,lng,address data)</li><li>Date Picker (jquery date picker, options for format, api returns string)</li><li>Color Picker (WP color swatch picker)</li><li>Tab (Group fields into tabs)</li><li>Message (Render custom messages into the fields)</li><li>Repeater (ability to create repeatable blocks of fields!)</li><li>Flexible Content (ability to create flexible blocks of fields!)</li><li>Gallery (Add, edit and order multiple images in 1 simple field)</li><li>[Custom](<a href=\"http://www.advancedcustomfields.com/resources/tutorials/creating-a-new-field-type/)\">www.advancedcustomfields.com/resources/tutorials/creating-a-new-field-type/)</a> (Create your own field type!)</li></ul>\n<h4> Tested on </h4>\n<ul><li>Mac Firefox 	:)</li><li>Mac Safari 	:)</li><li>Mac Chrome	:)</li><li>PC Safari 	:)</li><li>PC Chrome		:)</li><li>PC Firefox	:)</li><li>iPhone Safari :)</li><li>iPad Safari 	:)</li><li>PC ie7		:S</li></ul>\n<h4> Website </h4>\n<p><a href=\"http://www.advancedcustomfields.com/\">www.advancedcustomfields.com/</a></p>\n<h4> Documentation </h4>\n<ul><li>[Getting Started](<a href=\"http://www.advancedcustomfields.com/resources/#getting-started)\">www.advancedcustomfields.com/resources/#getting-started)</a></li><li>[Field Types](<a href=\"http://www.advancedcustomfields.com/resources/#field-types)\">www.advancedcustomfields.com/resources/#field-types)</a></li><li>[Functions](<a href=\"http://www.advancedcustomfields.com/resources/#functions)\">www.advancedcustomfields.com/resources/#functions)</a></li><li>[Actions](<a href=\"http://www.advancedcustomfields.com/resources/#actions)\">www.advancedcustomfields.com/resources/#actions)</a></li><li>[Filters](<a href=\"http://www.advancedcustomfields.com/resources/#filters)\">www.advancedcustomfields.com/resources/#filters)</a></li><li>[How to guides](<a href=\"http://www.advancedcustomfields.com/resources/#how-to)\">www.advancedcustomfields.com/resources/#how-to)</a></li><li>[Tutorials](<a href=\"http://www.advancedcustomfields.com/resources/#tutorials)\">www.advancedcustomfields.com/resources/#tutorials)</a></li></ul>\n<h4> Bug Submission and Forum Support </h4>\n<p><a href=\"http://support.advancedcustomfields.com/\">support.advancedcustomfields.com/</a></p>\n<h4> Please Vote and Enjoy </h4>\n<p>Your votes really make a difference! Thanks.</p>\n\";s:12:\"installation\";s:467:\"<ol><li>Upload <code>advanced-custom-fields</code> to the <code>/wp-content/plugins/</code> directory</li><li>Activate the plugin through the <code>Plugins</code> menu in WordPress</li><li>Click on the new menu item \"Custom Fields\" and create your first Custom Field Group!</li><li>Your custom field group will now appear on the page / post / template you specified in the field group\'s location rules!</li><li>Read the documentation to display your data: </li></ol>\n\";s:9:\"changelog\";s:7541:\"<h4> 5.3.1 </h4>\n<ul><li>Flexible Content field: added toggle icons to show layout open/close state</li><li>Gallery field: Fixed bug where images could not save a blank title value</li><li>Taxonomy field: Added pagination when rendered as a Select2 element</li><li>Relationship field: Changed validation to better edit values when a minimum is set</li><li>Google map field: Fixed minor autocomplete bugs and added search icon</li><li>Message field: Added new_lines setting</li><li>Core: Added <code>*</code> to required fields when editing a field group</li><li>Core: Prevented updates to .json file when syncing</li><li>Core: Fields now render as div (instead of table) when labels are left aligned</li><li>Core: Minor fixes and improvements</li><li>Language: Updated .po headers - thanks to Ralf Koller</li></ul>\n<h4> 5.3.0 </h4>\n<ul><li>WYSIWYG field: Fixed <code>Visual/Text</code> toggle bug with WP 4.3</li><li>Select field: Fixed Select2 bug hiding selected choices</li></ul>\n<h4> 5.2.9 </h4>\n<ul><li>Field group: Added new <code>status</code> setting to enable/disable</li><li>Field group: Added new <code>description</code> setting shown to developers when viewing the field group list</li><li>Field group: Moved <code>Show field keys</code> Screen Option within existing <code>Show on Screen</code> checkboxes</li><li>Tab field: Fixed missing min-height to left aligned tab wrapper</li><li>Relationship field: Added timeout to reduce AJAX requests whilst typing in search</li><li>Flexible Content field: Fixed minor JS bug where removing a layout would not update the order numbers</li><li>Core: Fixed bug validating uppercase file extensions</li><li>Core: Renamed menu items</li><li>Core: Replace sprite icons with font</li><li>Core: Added new setting <code>export_textdomain</code> to add __() to generated export code</li><li>Core: Fixed conflict with Post Type Order plugin causing issues when querying posts</li><li>Core: Fixed conflict with WPML causing issues when querying posts</li><li>Core: Added compatibility for WP 4.3</li><li>Core: Minor fixes and improvements</li><li>Language: Updated German translation - thanks to Ralf Koller</li><li>Language: Updated Italian translation - thanks to Davide Pante</li></ul>\n<h4> 5.2.8 </h4>\n<ul><li>Image field: Added selection restrictions in media popup (width, height, size, type)</li><li>File field: Same as above</li><li>Gallery field: Same as above</li><li>Tab field: Added new <code>endpoint</code> setting - allows multiple tab groups</li><li>Tab field: Improved CSS/JS to allow individual tab groups to use different alignments (left/top)</li><li>Repeater field: Added logic to delete nested sub field values (grand children)</li><li>Options page: Added new <code>autoload</code> setting</li><li>Core: Added new filter <code>acf/prepare_field</code></li><li>Core: Added upload validation logic to ignore filetype case sensitivity</li><li>Core: Fixed upload issue when filesize restriction contained a decimal place</li><li>Core: Improved validation/save JS compatibility with 3rd party plugins</li><li>Core: Updated Select2 library to v3.5.2</li><li>Core: Fixed bug hiding Select2 choices when multiple found with the same label</li><li>Core: Minor fixes and improvements</li><li>Language: Updated Italian translation - thanks to Davide Pante & Francesco Mazzola</li><li>Language: Updated German translation - thanks to Ralf Koller</li><li>Language: Updating Finnish translation - thanks to Sauli Rajala</li></ul>\n<h4> 5.2.7 </h4>\n<ul><li>Taxonomy field: Split setting <code>load_save_terms</code> into <code>load_terms</code> and <code>save_terms</code></li><li>Select field: Fixed bug causing values containing <code>,</code> to fail</li><li>Checkbox field: Fixed bug causing values containing <code>,</code> to fail</li><li>Checkbox field: Added new <code>toggle all</code> setting</li><li>User field: Added new filters <code>acf/fields/user/result</code> and <code>acf/fields/user/search_columns</code></li><li>Gallery field: Added logic to increase sidebar width when space is available</li><li>Options page: Added new <code>post_id</code> setting to customise where values are loaded and saved</li><li>API: Improved `get_field()` to better handle no value</li><li>API: Optimised asset loading when using the `acf_form()` function</li><li>API: Added new function `delete_sub_field()`</li><li>Core: Added new `acf/init` action when ACF has loaded all functionality</li><li>Core: Added compatibility with Select2 language translations</li><li>Core: Changed compatibility filter default to false</li><li>Core: Minor fixes and improvements</li><li>Language: Updated German translation - thanks to Thomas Meyer</li><li>Language: Updated French Translation - thanks to Maxime Bernard-Jacquet</li><li>Language: Updated Persian translation - thanks to Kamel</li></ul>\n<h4> 5.2.6 </h4>\n<ul><li>Core: Improved validation logic to display HTML5 validation messages</li><li>Core: Improved conditional logic performance for large field groups</li><li>Core: Removed updates menu item when not activated as a plugin (included within theme)</li><li>Core: Fixed various JS performance issues</li><li>Core: Minor fixes and improvements</li><li>Core: Added compatibility for saving widget in <code>accessibility mode</code></li><li>Language: Added Finnish translation - thanks to Sauli Rajala</li></ul>\n<h4> 5.2.5 </h4>\n<ul><li>Core: Fixed JS error preventing changes to large field groups</li><li>Language: Added Romanian translation - thanks to Eduard Ungureanu</li></ul>\n<h4> 5.2.4 </h4>\n<ul><li>WYSIWYG field: Fixed bug where new editor is not focused when adding media</li><li>Core: Added new <code>uploader</code> setting to `acf_form()` options for basic upload inputs</li><li>Core: Fixed Multisite loop when WordPress MU Domain Mapping plugin is active</li><li>Core: Improved CSS when editing taxonomy terms and users</li><li>Core: Fixed validation bugs when clicking save or preview </li><li>Core: Added compatibility with WPML translations when field group is not set as a translatable post type</li><li>Core: Many minor fixes and improvements</li></ul>\n<h4> 5.2.3 </h4>\n<ul><li>Taxonomy field: Added button and popup to create new terms</li><li>Taxonomy field: Added new <code>Create Terms</code> setting to prevent popup</li><li>Core: Added network database upgrade admin page and functionality</li><li>Core: Increased required WP version from 3.5 to 3.6</li><li>Core: Fixed Select2 dependancy conflict with WooCommerce</li><li>Core: Fixed WPML bug where field group translation information is lost</li><li>Core: Fixed conditional logic bug where showing a parent field would override sub field conditional logic</li><li>Core: Minor fixes and improvements</li><li>Language: Updated Persian translation - thanks to Kamel</li></ul>\n<h4> 5.2.2 </h4>\n<ul><li>Image field: Fixed UI bug when image has been removed via media library</li><li>Relationship field: Added new minimum selection setting</li><li>Select field: Fixed bug when searching for a numeric value</li><li>Tab field: Fixed conditional logic bug</li><li>Field group: Added compatibility for custom status in <code>Post Status</code> location rule</li><li>Core: Added new `show_updates` setting to prevent plugin updates</li><li>Core: Added compatibility for 3rd party update management websites</li><li>Core: Added spinner when saving taxonomy term, user and front end form</li><li>Core: Minor fixes and improvements</li><li>Language: Updated Slovak translation - thanks to Jan Fajcak</li></ul>\n<p> </p>\n\";s:14:\"upgrade_notice\";s:550:\"<h4> 5.2.7 </h4>\n<ul><li>Field class names have changed slightly in v5.2.7 from `field_type-{$type}` to `acf-field-{$type}`. This change was introduced to better optimise JS performance. The previous class names can be added back in with the following filter: <a href=\"http://www.advancedcustomfields.com/resources/acfcompatibility/\">www.advancedcustomfields.com/resources/acfcompatibility/</a></li></ul>\n<h4> 3.0.0 </h4>\n<ul><li>Editor is broken in WordPress 3.3</li></ul>\n<h4> 2.1.4 </h4>\n<ul><li>Adds post_id column back into acf_values</li></ul>\n\";}','no'),(3876,'page_comments','0','yes'),(3878,'_site_transient_update_core','O:8:\"stdClass\":4:{s:7:\"updates\";a:1:{i:0;O:8:\"stdClass\":10:{s:8:\"response\";s:6:\"latest\";s:8:\"download\";s:59:\"https://downloads.wordpress.org/release/wordpress-4.3.1.zip\";s:6:\"locale\";s:5:\"en_US\";s:8:\"packages\";O:8:\"stdClass\":5:{s:4:\"full\";s:59:\"https://downloads.wordpress.org/release/wordpress-4.3.1.zip\";s:10:\"no_content\";s:70:\"https://downloads.wordpress.org/release/wordpress-4.3.1-no-content.zip\";s:11:\"new_bundled\";s:71:\"https://dow